// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

library Base64Decode {
    bytes constant private base64urlchars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-_=";

    function decode(string memory _str) internal pure returns (string memory) {
        require( (bytes(_str).length % 4) == 0, "Length not multiple of 4");
        bytes memory _bs = bytes(_str);

        uint i = 0;
        uint j = 0;
        uint dec_length = (_bs.length/4) * 3;
        bytes memory dec = new bytes(dec_length);

        for (; i< _bs.length; i+=4 ) {
            (dec[j], dec[j+1], dec[j+2]) = dencode4(
                bytes1(_bs[i]),
                bytes1(_bs[i+1]),
                bytes1(_bs[i+2]),
                bytes1(_bs[i+3])
            );
            j += 3;
        }
        while (dec[--j]==0)
            {}

        bytes memory res = new bytes(j+1);
        for (i=0; i<=j;i++)
            res[i] = dec[i];

        return string(res);
    }


    function dencode4 (bytes1 b0, bytes1 b1, bytes1 b2, bytes1 b3) private pure returns (bytes1 a0, bytes1 a1, bytes1 a2)
    {
        uint pos0 = charpos(b0);
        uint pos1 = charpos(b1);
        uint pos2 = charpos(b2)%64;
        uint pos3 = charpos(b3)%64;

        a0 = bytes1(uint8(( pos0 << 2 | pos1 >> 4 )));
        a1 = bytes1(uint8(( (pos1&15)<<4 | pos2 >> 2)));
        a2 = bytes1(uint8(( (pos2&3)<<6 | pos3 )));
    }

    function charpos(bytes1 char) private pure returns (uint pos) {
        for (; base64urlchars[pos] != char; pos++)
            {}    //for loop body is not necessary
        require (base64urlchars[pos]==char, "Illegal char in string");
        return pos;
    }

}

contracts/CDH.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import { ERC721 } from "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import { IERC721 } from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import { ERC721Enumerable } from "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import { ERC721Burnable } from "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import { Base64 } from "@openzeppelin/contracts/utils/Base64.sol";
import { Base64Decode } from "./base64.sol";
import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";
import { ICDH } from "./interfaces/ICDH.sol";
import { IERC6551Registry } from "./interfaces/IERC6551Registry.sol";

contract CDH is ICDH, ERC721, ERC721Enumerable, ERC721Burnable {
	// =============================================================
	//                           STORAGE
	// =============================================================

	uint256 private _nextTokenId;
	address public erc6551Account;
	address public erc6551Registry;

	// tokenId => fieldNumber => fieldValue
	mapping(uint256 => mapping(uint256 => string)) public override metadatas;
	mapping(uint256 => mapping(address => mapping(uint256 => bool)))
		public
		override permittedEditors;
	mapping(uint256 => string) public override fieldNames;
	mapping(uint256 => address) public override fieldOwners;
	// tokenId => metadata
	mapping(uint256 => string) public override tokenMetadatas;
	// EOA address => tokenId
	mapping(address => uint256) public override eoaToTokenId;

	// tokenId => tbaAccount
	mapping(uint256 => address) public override tokenIdToTbaAccount;
	// EOA address => tbaAccount
	mapping(address => address) public override eoaToTbaAccount;

	// =============================================================
	//                          MODIFIER
	// =============================================================

	/// @notice only owner or permitted
	/// @param tokenId token id
	modifier onlyOwnerOrPermitted(uint256 tokenId, uint256 fieldNumber) {
		if (
			ERC721.ownerOf(tokenId) != msg.sender &&
			permittedEditors[tokenId][msg.sender][fieldNumber] == false
		) {
			revert NotTokenOwnerOrPermitted(tokenId);
		}
		_;
	}

	/// @notice only field owner
	/// @param fieldNumber field number
	modifier onlyFieldOwner(uint256 fieldNumber) {
		if (fieldOwners[fieldNumber] != msg.sender) {
			revert NotFieldOwner(
				fieldNumber,
				msg.sender,
				fieldOwners[fieldNumber]
			);
		}
		_;
	}

	/// @notice not own nft
	/// @param to to
	modifier notOwnNFT(address to) {
		if (balanceOf(to) > 0) {
			revert AlreadyOwnsNFT(to);
		}
		_;
	}

	// =============================================================
	//                          CONSTRUCTOR
	// =============================================================

	constructor(
		string memory name,
		string memory symbol,
		address _erc6551Registry,
		address _erc6551Account
	) ERC721(name, symbol) {
		erc6551Registry = _erc6551Registry;
		erc6551Account = _erc6551Account;
	}

	// =============================================================
	//                         EXTERNAL WRITE
	// =============================================================

	/// @notice safe mint
	/// @param to to
	/// @param uri uri
	function safeMint(address to, string memory uri) public notOwnNFT(to) {
		uint256 tokenId = _nextTokenId++;
		_safeMint(to, tokenId);
		address tbaAccount = IERC6551Registry(erc6551Registry).createAccount(
			erc6551Account,
			bytes32(tokenId),
			5555,
			address(this),
			tokenId
		);
		tokenIdToTbaAccount[tokenId] = tbaAccount;
		eoaToTbaAccount[to] = tbaAccount;
		eoaToTokenId[to] = tokenId;

		// save DACS or ...
		tokenMetadatas[tokenId] = string(
			abi.encodePacked(
				"data:application/json;base64,",
				Base64.encode(
					bytes(
						abi.encodePacked(
							'{"name": "CDH #',
							Strings.toString(tokenId),
							'", "description": "CDH NFT", "image": "',
							uri,
							'"}'
						)
					)
				)
			)
		);
	}

	/// @notice set permitted editor
	/// @param tokenId token id
	/// @param editor editor address
	/// @param fieldNumber field number
	/// @param permission permission
	function setPermittedEditor(
		uint256 tokenId,
		address editor,
		uint256 fieldNumber,
		bool permission
	) public override(ICDH) {
		if (ERC721.ownerOf(tokenId) != msg.sender) {
			revert UnauthorizedCaller();
		}
		permittedEditors[tokenId][editor][fieldNumber] = permission;
		emit EditorPermissionChanged(tokenId, editor, permission);
	}

	/// @notice add metadata
	/// @param tokenId token id
	/// @param fieldNumber field number
	/// @param fieldValue field value
	function addMetadata(
		uint256 tokenId,
		uint256 fieldNumber,
		string memory fieldValue
	) public override(ICDH) onlyOwnerOrPermitted(tokenId, fieldNumber) {
		if (bytes(metadatas[tokenId][fieldNumber]).length != 0) {
			revert MetadataAlreadyExists(tokenId, fieldNumber);
		}
		metadatas[tokenId][fieldNumber] = fieldValue;
		// add field to DACS or ...
		_addTokenMetadata(tokenId, fieldNumber, fieldValue);
		emit MetadataAdded(tokenId, fieldNumber, fieldValue);
	}

	/// @notice update metadata
	/// @param tokenId token id
	/// @param fieldNumber field number
	/// @param newValue new value
	function updateMetadata(
		uint256 tokenId,
		uint256 fieldNumber,
		string memory newValue
	) public override(ICDH) onlyOwnerOrPermitted(tokenId, fieldNumber) {
		if (bytes(metadatas[tokenId][fieldNumber]).length == 0) {
			revert MetadataDoesNotExist(tokenId, fieldNumber);
		}
		metadatas[tokenId][fieldNumber] = newValue;
		emit MetadataUpdated(tokenId, fieldNumber, newValue);
	}

	/// @notice register field name
	/// @param fieldNumber field number
	/// @param fieldName field name
	function registerFieldName(
		uint256 fieldNumber,
		string calldata fieldName
	) public override {
		if (bytes(fieldNames[fieldNumber]).length != 0) {
			revert FieldNameAlreadyExists(fieldNumber);
		}
		fieldNames[fieldNumber] = fieldName;
		fieldOwners[fieldNumber] = msg.sender;
	}

	/// @notice update field name
	/// @param fieldNumber field number
	/// @param newFieldName new field name
	function updateFieldName(
		uint256 fieldNumber,
		string calldata newFieldName
	) public onlyFieldOwner(fieldNumber) {
		if (bytes(fieldNames[fieldNumber]).length == 0) {
			revert FieldNameDoesNotExist(fieldNumber);
		}
		if (bytes(newFieldName).length == 0) {
			revert InvalidFieldName(newFieldName);
		}
		fieldNames[fieldNumber] = newFieldName;
	}

	// =============================================================
	//                          INTERNAL WRITE
	// =============================================================

	/// @notice add token metadata
	/// @param tokenId token id
	/// @param fieldNumber field number
	/// @param newValue new value
	function _addTokenMetadata(
		uint256 tokenId,
		uint256 fieldNumber,
		string memory newValue
	) internal {
		// Decode the existing metadata
		string memory decodedMetadata = Base64Decode.decode(
			tokenMetadatas[tokenId]
		);

		// Remove the trailing '}' character to add new fields
		bytes memory trimmedMetadataBytes = bytes(decodedMetadata);
		trimmedMetadataBytes[trimmedMetadataBytes.length - 1] = ""; // Remove last character

		// Create new metadata with additional field
		string memory newMetadata = string(
			abi.encodePacked(
				trimmedMetadataBytes,
				', "',
				Strings.toString(fieldNumber),
				'": "',
				newValue,
				'"}'
			)
		);

		// Encode the updated metadata
		tokenMetadatas[tokenId] = string(
			abi.encodePacked(
				"data:application/json;base64,",
				Base64.encode(bytes(newMetadata))
			)
		);
	}

	/// @notice update
	/// @param to to
	/// @param tokenId token id
	/// @param auth auth
	function _update(
		address to,
		uint256 tokenId,
		address auth
	) internal override(ERC721, ERC721Enumerable) returns (address) {
		return super._update(to, tokenId, auth);
	}

	/// @notice increase balance
	/// @param account account
	/// @param value value
	function _increaseBalance(
		address account,
		uint128 value
	) internal override(ERC721, ERC721Enumerable) {
		super._increaseBalance(account, value);
	}

	// =============================================================
	//                         EXTERNAL VIEW
	// =============================================================

	/// @notice get metadata
	/// @param tokenId token id
	/// @param fieldNumber field number
	function getMetadata(
		uint256 tokenId,
		uint256 fieldNumber
	) public view override(ICDH) returns (string memory) {
		return metadatas[tokenId][fieldNumber];
	}

	/// @notice get owners
	/// @param amount amount
	/// @param from starting index
	function getOwners(
		uint256 amount,
		uint256 from
	) public view returns (address[] memory, address[] memory) {
		uint256 total = totalSupply();
		if (from >= total) {
			return (new address[](0), new address[](0));
		}

		uint256 validAmount = (from + amount > total) ? total - from : amount;
		address[] memory owners = new address[](validAmount);
    	address[] memory tbaAccounts = new address[](validAmount);

		for (uint256 i = 0; i < validAmount; i++) {
			address owner = ownerOf(from + i);
			owners[i] = owner;
			tbaAccounts[i] = eoaToTbaAccount[owner];
		}

		return (owners, tbaAccounts);
	}

	/// @notice token uri
	/// @param tokenId token id
	function tokenURI(
		uint256 tokenId
	) public view override(ICDH, ERC721) returns (string memory) {
		return super.tokenURI(tokenId);
	}

	/// @notice supports interface
	/// @param interfaceId interface id
	function supportsInterface(
		bytes4 interfaceId
	) public view override(ERC721, ERC721Enumerable) returns (bool) {
		return super.supportsInterface(interfaceId);
	}

	// =============================================================
	//                         SBT
	// =============================================================

	/// @notice Override to disable transfer functionality
	function setApprovalForAll(
		address operator,
		bool approved
	) public pure override(ERC721, IERC721) {
		revert SoulboundToken();
	}

	function approve(
		address to,
		uint256 tokenId
	) public pure override(ERC721, IERC721) {
		revert SoulboundToken();
	}

	function transferFrom(
		address from,
		address to,
		uint256 tokenId
	) public pure override(ERC721, IERC721) {
		revert SoulboundToken();
	}

	function safeTransferFrom(
		address from,
		address to,
		uint256 tokenId,
		bytes memory _data
	) public pure override(ERC721, IERC721) {
		revert SoulboundToken();
	}
}

contracts/interfaces/ICDH.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

interface ICDH {
	// =============================================================
	//                           ERROR
	// =============================================================

	error NotTokenOwnerOrPermitted(uint256 tokenId);
	error UnauthorizedCaller();
	error MetadataAlreadyExists(uint256 tokenId, uint256 fieldNumber);
	error MetadataDoesNotExist(uint256 tokenId, uint256 fieldNumber);
	error FieldNameAlreadyExists(uint256 fieldNumber);
	error FieldNameDoesNotExist(uint256 fieldNumber);
	error NotFieldOwner(
		uint256 fieldNumber,
		address invalidOwner,
		address fieldOwner
	);
	error InvalidFieldName(string fieldName);
	error AlreadyOwnsNFT(address owner);
	error SoulboundToken();

	// =============================================================
	//                           EVENT
	// =============================================================

	event EditorPermissionChanged(
		uint256 indexed tokenId,
		address indexed editor,
		bool permission
	);
	event MetadataAdded(
		uint256 indexed tokenId,
		uint256 fieldNumber,
		string fieldValue
	);
	event MetadataUpdated(
		uint256 indexed tokenId,
		uint256 fieldNumber,
		string newValue
	);

	// =============================================================
	//                         EXTERNAL WRITE
	// =============================================================

	function safeMint(address to, string calldata uri) external;

	function setPermittedEditor(
		uint256 tokenId,
		address editor,
		uint256 fieldNumber,
		bool permission
	) external;

	function addMetadata(
		uint256 tokenId,
		uint256 fieldNumber,
		string calldata fieldValue
	) external;

	function updateMetadata(
		uint256 tokenId,
		uint256 fieldNumber,
		string calldata newValue
	) external;

	function registerFieldName(
		uint256 fieldNumber,
		string calldata fieldName
	) external;

	function updateFieldName(
		uint256 fieldNumber,
		string calldata newFieldName
	) external;

	// =============================================================
	//                         EXTERNAL VIEW
	// =============================================================

	function getMetadata(
		uint256 tokenId,
		uint256 fieldNumber
	) external view returns (string memory);

	function tokenURI(uint256 tokenId) external view returns (string memory);

	function metadatas(
		uint256 tokenId,
		uint256 fieldId
	) external view returns (string memory);

	function permittedEditors(
		uint256 tokenId,
		address editor,
		uint256 fieldId
	) external view returns (bool);

	function fieldNames(uint256 fieldId) external view returns (string memory);

	function fieldOwners(uint256 fieldId) external view returns (address);

	function tokenMetadatas(
		uint256 tokenId
	) external view returns (string memory);

	function eoaToTokenId(address eoa) external view returns (uint256);

	function tokenIdToTbaAccount(uint256 tokenId) external view returns (address);

	function eoaToTbaAccount(address eoa) external view returns (address);
}

contracts/interfaces/IERC6551Registry.so.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

interface IERC6551Registry {
    /**
     * @dev The registry MUST emit the ERC6551AccountCreated event upon successful account creation.
     */
    event ERC6551AccountCreated(
        address account,
        address indexed implementation,
        bytes32 salt,
        uint256 chainId,
        address indexed tokenContract,
        uint256 indexed tokenId
    );

    /**
     * @dev The registry MUST revert with AccountCreationFailed error if the create2 operation fails.
     */
    error AccountCreationFailed();

    /**
     * @dev Creates a token bound account for a non-fungible token.
     *
     * If account has already been created, returns the account address without calling create2.
     *
     * Emits ERC6551AccountCreated event.
     *
     * @return account The address of the token bound account
     */
    function createAccount(
        address implementation,
        bytes32 salt,
        uint256 chainId,
        address tokenContract,
        uint256 tokenId
    ) external returns (address account);

    /**
     * @dev Returns the computed token bound account address for a non-fungible token.
     *
     * @return account The address of the token bound account
     */
    function account(
        address implementation,
        bytes32 salt,
        uint256 chainId,
        address tokenContract,
        uint256 tokenId
    ) external view returns (address account);
}
